  <body id="ibody-2-2" class="gjs-t-body"><!-- NAV --><section id="inav-2-2" class="gjs-section"><div id="inavcnt-2-2-2" class="gjs-container"><div id="ip985t-2-2" class="navbar"><div data-gjs="navbar" id="i1gbgk" class="navbar-cnt"><a href="/" id="im678l" class="navbar-brand"><h1 id="ilogo-2-2">Sovereign Tech</h1></a><div data-gjs="navbar-items" id="i6kzoa" class="navbar-items-c"><nav id="iot8xg" class="navbar-menu-nav"><a id="ilink1-3-2" href="#builder" class="nav-links">Builder</a><a id="ilink1-2-2-2" href="#contact" class="nav-links">Contact</a><a id="ilink2-2-2" href="#footer" class="nav-links">About</a></nav></div></div></div></div></section><!-- BUILDER --><form method="get" id="pcForm"></form><!-- FOOTER --><section id="nav-simple"><nav role="navigation" aria-label="Primary" id="ih0mx7" class="nb-bar"></nav></section><section id="iabout-2-2" class="gjs-section"><div id="iaboutcnt-2-2" class="gjs-container"><div data-type-role="flex-row" id="iwr5kl-2-2" class="gjs-plg-flex-row"><div data-type-role="flex-column" id="ighlav-2-2" class="gjs-plg-flex-column"><h1>Basic Gaming Computer</h1><div>For Basic Gaming</div><img id="i7pqug-2-2-2" src="https://cdn.grapesjs.com/workspaces/cmjzr0yh01imsk7gh61l2ruwa/assets/6b1a4218-2867-4e67-8704-0c31bbdbc65c__chatgpt-image-jan-4-2026-113143-pm.png"/><input type="radio" id="ip16ze" name="build-tier" value="Basic" aria-label="Select Basic Gaming Computer"/><label for="ip16ze" class="radio-card"><span aria-hidden="true" class="radio-mark"></span><span class="radio-label">Select Basic</span></label></div><div data-type-role="flex-column" id="i7kv24-2-2" class="gjs-plg-flex-column"><h1>Standard Gaming Computer</h1><div>For Most Gaming</div><img id="i7pqug-4-2" src="https://cdn.grapesjs.com/workspaces/cmjzr0yh01imsk7gh61l2ruwa/assets/d75ecba4-ca3d-4928-9a5d-c6092f45849e__basic.png"/><input type="radio" id="i1m1ku" name="build-tier" value="Standard" aria-label="Select Standard Gaming Computer"/><label for="i1m1ku" class="radio-card"><span aria-hidden="true" class="radio-mark"></span><span class="radio-label">Select Standard</span></label></div><div data-type-role="flex-column" id="ihfkvp-2-2" class="gjs-plg-flex-column"><h1 id="ii3uhl">Pro Gaming Computer </h1><div>For All gaming at high FPS</div><img id="i7pqug-3-2-2" src="https://cdn.grapesjs.com/workspaces/cmjzr0yh01imsk7gh61l2ruwa/assets/d75ecba4-ca3d-4928-9a5d-c6092f45849e__basic.png"/><input type="radio" id="i3vtwo" name="build-tier" value="Pro" aria-label="Select Pro Gaming Computer"/><label for="i3vtwo" class="radio-card"><span aria-hidden="true" class="radio-mark"></span><span class="radio-label">Select Pro</span></label></div></div></div></section><section id="ihero-2-2" class="gjs-section"><div id="iherocnt-2-2" class="gjs-container"><div id="builder" class="grid"><!-- LEFT: FORM --><div id="ijwo3r" class="card"><div class="card"><h1 id="itfftb">Build a Custom Gaming PC</h1><p id="io2slf" class="muted">Pick your parts. We’ll generate a summary + estimate you can send to the shop.</p><form method="get" id="pcForm-2"><div id="i962eq" class="two"><div id="ivyrl1" class="field"><label for="name" id="io2u7c">Name</label><input type="text" id="name" placeholder="Your name" required/></div><div id="ijni7r" class="field"><label for="email" id="i6q2fs">Email</label><input type="email" id="email" placeholder="you@email.com" required/></div></div><div id="ieok28" class="two"><div id="izyzqv" class="field"><label for="cpu" id="izf07k">CPU</label><select type="text" required id="cpu"><option value="" data-price="0" id="ism3vk">Select CPU</option><option value="Intel Core i5" data-price="300" id="i6bb2d">Intel Core i5</option><option value="Intel Core i7" data-price="480" id="ish623">Intel Core i7</option><option value="AMD Ryzen 5" data-price="290" id="iuvvdg">AMD Ryzen 5</option><option value="AMD Ryzen 7" data-price="470" id="iypqiy">AMD Ryzen 7</option></select></div><div id="inb60j" class="field"><label for="gpu" id="ijp3fk">GPU</label><select type="text" required id="gpu"><option value="" data-price="0" id="idixxh">Select GPU</option><option value="RTX 4060" data-price="520" id="ix4nmg">NVIDIA RTX 4060</option><option value="RTX 4070" data-price="850" id="i5y08r">NVIDIA RTX 4070</option><option value="RX 7600" data-price="470" id="ic8dan">AMD RX 7600</option><option value="RX 7800 XT" data-price="980" id="ipurfx">AMD RX 7800 XT</option></select></div></div><div id="iv48p5" class="two"><div id="izwrnl" class="field"><label for="ram" id="i9bllk">RAM</label><select type="text" required id="ram"><option value="" data-price="0" id="i1jjmh">Select RAM</option><option value="16GB DDR4" data-price="85" id="il8kef">16GB DDR4</option><option value="32GB DDR4" data-price="150" id="i983yk">32GB DDR4</option><option value="16GB DDR5" data-price="120" id="im78ti">16GB DDR5</option><option value="32GB DDR5" data-price="210" id="i78fhj">32GB DDR5</option></select></div><div id="i2apbl" class="field"><label for="storage" id="ig57au">Storage</label><select type="text" required id="storage"><option value="" data-price="0" id="ixjvhs">Select Storage</option><option value="1TB NVMe SSD" data-price="120" id="ic3y8a">1TB NVMe SSD</option><option value="2TB NVMe SSD" data-price="220" id="i54ful">2TB NVMe SSD</option><option value="1TB SSD + 2TB HDD" data-price="190" id="ixhk55">1TB SSD + 2TB HDD</option></select></div></div><div id="i9yjxc" class="two"><div id="irui0g" class="field"><label for="cooling" id="i7f5ow">Cooling</label><select type="text" required id="cooling"><option value="" data-price="0" id="iaiboy">Select Cooling</option><option value="Air Cooler" data-price="60" id="i8gmm1">Air Cooler</option><option value="240mm AIO" data-price="160" id="i9iotr">240mm AIO Liquid</option><option value="360mm AIO" data-price="240" id="i0xlom">360mm AIO Liquid</option></select></div><div id="iobcaw" class="field"><label for="psu" id="inz4iq">PSU</label><select type="text" required id="psu"><option value="" data-price="0" id="izy5ou">Select PSU</option><option value="650W 80+ Gold" data-price="120" id="ifpvng">650W 80+ Gold</option><option value="750W 80+ Gold" data-price="150" id="ijd5au">750W 80+ Gold</option><option value="850W 80+ Gold" data-price="190" id="i88dzj">850W 80+ Gold</option></select></div></div><div id="izemlk" class="two"><div id="i6m882" class="field"><label for="case" id="ihed1s">Case</label><select type="text" required id="case"><option value="" data-price="0" id="icbxr1">Select Case</option><option value="Mid Tower (Airflow)" data-price="120" id="iegbuk">Mid Tower (Airflow)</option><option value="Mid Tower (RGB)" data-price="160" id="izyplf">Mid Tower (RGB)</option><option value="Compact ATX" data-price="110" id="i1ws8f">Compact ATX</option></select></div><div id="izrb9i" class="field"><label for="os" id="iteun5">Operating System</label><select type="text" required id="os"><option value="" data-price="0" id="i7fyin">Select OS</option><option value="Windows 11 Home" data-price="170" id="ilalxa">Windows 11 Home</option><option value="Windows 11 Pro" data-price="240" id="i081va">Windows 11 Pro</option><option value="No OS" data-price="0" id="i4vfie">No OS</option></select></div></div><div id="iyonth" class="field"><label for="extras" id="i2sqj3">Extras</label><select type="text" id="extras"><option value="None" data-price="0" id="isqvoj">None</option><option value="Wi-Fi + Bluetooth" data-price="60" id="iha7kg">Wi-Fi + Bluetooth</option><option value="Extra RGB Fans" data-price="50" id="itwf2n">Extra RGB Fans</option><option value="Premium Cable Sleeves" data-price="45" id="ij1xr9">Premium Cable Sleeves</option></select><span id="ie78b2" class="muted">Tip: You can add notes below for games you play, target FPS, etc.</span></div><div id="contact" class="field"><label for="message" id="iy5ok5">Message / Requirements</label><textarea id="message" placeholder="e.g. I play Valorant + Warzone, want 1440p, quiet build, black/red theme..."></textarea></div><div class="form-actions"></div></form></div></div><!-- RIGHT: SUMMARY --><div id="ien8he" class="card"><h2 id="iuwa0l">Your Build Summary</h2><div id="summary"></div><div id="i8ium5"><div id="iy5jyj" class="summary-row"><span id="ip6udg" class="muted">Estimated Total</span><span id="total" class="total">$0</span></div></div><div><div id="izpntl" class="summary-row"><button type="submit" id="izebch" class="btn"><a href="#builder" aria-label="Scroll to form to review and submit" class="btn">Review &amp; Submit</a></button></div></div><div id="iad94f"><pre id="output"></pre><pre id="output-2"></pre></div></div></div></div></section><section id="ifooter-2-2" class="gjs-section"><div id="ifootercnt-2-2-2" class="gjs-container"><div id="ifootcopy-2-2-2"><span id="ifootcopytext-2-2-2">Copyright 2026 Sovereign Tech. All Rights Reserved.</span></div></div></section><script id="iad1tf">
      // Scope the script to the visible, consolidated form inside the builder card
      const form = document.querySelector('#ijwo3r form');

      function field(id) {
        return form.querySelector('#' + id);
      }

      const cpu = field('cpu');
      const gpu = field('gpu');
      const ram = field('ram');
      const storage = field('storage');
      const cooling = field('cooling');
      const psu = field('psu');
      const pcCase = field('case');
      const os = field('os');
      const extras = field('extras');

      const selects = [cpu, gpu, ram, storage, cooling, psu, pcCase, os, extras];

      const summaryEl = document.getElementById('summary');
      const totalEl = document.getElementById('total');
      const outputEl = document.getElementById('output');

      function getSelectedText(sel) {
        return sel.value || '—';
      }

      function getSelectedPrice(sel) {
        const opt = sel.options[sel.selectedIndex];
        return Number(opt?.dataset?.price || 0);
      }

      function money(n) {
        return `$${n.toLocaleString('en-AU')}`;
      }

      function renderSummary() {
        const rows = [
          ['CPU', getSelectedText(cpu), getSelectedPrice(cpu)],
          ['GPU', getSelectedText(gpu), getSelectedPrice(gpu)],
          ['RAM', getSelectedText(ram), getSelectedPrice(ram)],
          ['Storage', getSelectedText(storage), getSelectedPrice(storage)],
          ['Cooling', getSelectedText(cooling), getSelectedPrice(cooling)],
          ['PSU', getSelectedText(psu), getSelectedPrice(psu)],
          ['Case', getSelectedText(pcCase), getSelectedPrice(pcCase)],
          ['OS', getSelectedText(os), getSelectedPrice(os)],
          ['Extras', getSelectedText(extras), getSelectedPrice(extras)],
        ];

        summaryEl.innerHTML = rows.map(([label, val, price]) => `
      <div class="summary-row">
        <span class="muted">${label}</span>
        <span>${val}</span>
        <span class="muted">${price ? money(price) : ''}</span>
      </div>
    `).join('');

        const total = rows.reduce((sum, r) => sum + r[2], 0);
        totalEl.textContent = money(total);
      }

      selects.forEach(sel => sel.addEventListener('change', renderSummary));
      renderSummary();

      form.addEventListener('submit', (e) => {
        e.preventDefault();

        const data = {
          name: field('name').value.trim(),
          email: field('email').value.trim(),
          cpu: cpu.value,
          gpu: gpu.value,
          ram: ram.value,
          storage: storage.value,
          cooling: cooling.value,
          psu: psu.value,
          case: pcCase.value,
          os: os.value,
          extras: extras.value,
          message: field('message').value.trim(),
          estimate: totalEl.textContent
        };

        outputEl.textContent =
          `NEW BUILD REQUEST
Name: ${data.name}
Email: ${data.email}

SPECS:
- CPU: ${data.cpu}
- GPU: ${data.gpu}
- RAM: ${data.ram}
- Storage: ${data.storage}
- Cooling: ${data.cooling}
- PSU: ${data.psu}
- Case: ${data.case}
- OS: ${data.os}
- Extras: ${data.extras}

Notes:
${data.message || "(none)"}

Estimated Total: ${data.estimate}
`;
      });
    </script></body>
